mapper.select:
select
    company.*,
    (select count(user_role.id)
     from user_role
     where company.id = user_role.source_id)       as countCompanyAdmin,
    (select count(department.id)
     from department
     where department.company_id = company.id) as countDept
from company


        and company.id = #{map.id}


        and company.name like #{map.name}


        and company.email like #{map.email}


        order by ${map.sort}
